        public void consomeTokens(){
                Token t = getToken(1);
                while( t.kind != SCHEDULER && t.kind != STATIC && t.kind != DYNAMIC && t.kind != TASK && t.kind != RESOURCE && t.kind != EOF){
                        getNextToken();
                        t = getToken(1);
                }
        }

